public class BinaryConnectionFactory extends DefaultConnectionFactory { public BinaryConnectionFactory() { super(); } public BinaryConnectionFactory(int len, int bufSize) { super(len, bufSize); } public BinaryConnectionFactory(int len, int bufSize, HashAlgorithm hash) { super(len, bufSize, hash); } @Override public MemcachedNode createMemcachedNode(SocketAddress sa, SocketChannel c, int bufSize) { boolean doAuth = false; if (getAuthDescriptor() != null) { doAuth = true; } return new BinaryMemcachedNodeImpl(sa, c, bufSize, createReadOperationQueue(), createWriteOperationQueue(), createOperationQueue(), getOpQueueMaxBlockTime(), doAuth, getOperationTimeout(), getAuthWaitTime(), this); } @Override public OperationFactory getOperationFactory() { return new BinaryOperationFactory(); } @Override protected String getName() { return "BinaryConnectionFactory"; } }