public class ClassDefFormat1 extends ClassDef { private int startGlyph; private int glyphCount; private int[] classValues; public ClassDefFormat1(RandomAccessFileEmulator raf) throws IOException { startGlyph = raf.readUnsignedShort(); glyphCount = raf.readUnsignedShort(); classValues = new int[glyphCount]; for (int i = 0; i < glyphCount; i++) { classValues[i] = raf.readUnsignedShort(); } } public int getFormat() { return 1; } }