public class PwDateTest { @Test public void testDate() { PwDate jDate = new PwDate(System.currentTimeMillis()); PwDate intermediate = (PwDate) jDate.clone(); PwDate cDate = new PwDate(intermediate.getCDate(), 0); assertTrue("jDate and intermediate not equal", jDate.equals(intermediate)); assertTrue("jDate and cDate not equal", cDate.equals(jDate)); } }