public class TestUtil { public static void extractKey ( Context ctx , String asset , String target ) throws Exception { InputStream key = ctx . getAssets ( ) . open ( asset , AssetManager . ACCESS_STREAMING ) ; FileOutputStream keyFile = new FileOutputStream ( target ) ; while ( true ) { byte [ ] buf = new byte [ 1024 ] ; int read = key . read ( buf ) ; if ( read == -1 ) { break ; } else { keyFile . write ( buf , 0 , read ) ; } } keyFile . close ( ) ; } public static InputStream getKeyFileInputStream ( Context ctx , String keyfile ) throws FileNotFoundException { InputStream keyIs = null ; if ( !EmptyUtils . isNullOrEmpty ( keyfile ) ) { Uri uri = UriUtil . parseDefaultFile ( keyfile ) ; keyIs = UriUtil . getUriInputStream ( ctx , uri ) ; } return keyIs ; } public static String getAppPath ( Context ctx , String filename ) { File file = new File ( ctx . getFilesDir ( ) , filename ) ; return file . getAbsolutePath ( ) ; } }