public class StrUtilTest { private final String text = "AbCdEfGhIj" ; private final String search = "BcDe" ; private final String badSearch = "Ed" ; @ Test public void testIndexOfIgnoreCase1 ( ) { assertEquals ( 1 , StrUtil . indexOfIgnoreCase ( text , search , Locale . ENGLISH ) ) ; } @ Test public void testIndexOfIgnoreCase2 ( ) { assertEquals ( -1 , StrUtil . indexOfIgnoreCase ( text , search , Locale . ENGLISH ) , 2 ) ; } @ Test public void testIndexOfIgnoreCase3 ( ) { assertEquals ( -1 , StrUtil . indexOfIgnoreCase ( text , badSearch , Locale . ENGLISH ) ) ; } private final String repText = "AbCtestingaBc" ; private final String repSearch = "ABc" ; private final String repSearchBad = "CCCCCC" ; private final String repNew = "12345" ; private final String repResult = "12345testing12345" ; @ Test public void testReplaceAllIgnoresCase1 ( ) { assertEquals ( repResult , StrUtil . replaceAllIgnoresCase ( repText , repSearch , repNew , Locale . ENGLISH ) ) ; } @ Test public void testReplaceAllIgnoresCase2 ( ) { assertEquals ( repText , StrUtil . replaceAllIgnoresCase ( repText , repSearchBad , repNew , Locale . ENGLISH ) ) ; } }