public class ProtectedString { private String string; private boolean protect; public boolean isProtected() { return protect; } public int length() { if (string == null) { return 0; } return string.length(); } public ProtectedString() { this(false, ""); } public ProtectedString(boolean enableProtection, String string) { protect = enableProtection; this.string = string; } public String toString() { return string; } }