public class KdfFactory { public static List<KdfEngine> kdfList = new ArrayList<KdfEngine>(); static { kdfList.add(new AesKdf()); kdfList.add(new Argon2Kdf(Argon2Kdf.Argon2Type.D)); kdfList.add(new Argon2Kdf(Argon2Kdf.Argon2Type.ID)); } public static KdfParameters getDefaultParameters() { return kdfList.get(0).getDefaultParameters(); } public static KdfEngine get(UUID uuid) { for (KdfEngine engine: kdfList) { if (engine.uuid.equals(uuid)) { return engine; } } return null; } }