public class BrowserDialog extends Dialog { public BrowserDialog(Context context) { super(context); } @Override protected void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); setContentView(R.layout.browser_install); setTitle(R.string.file_browser); Button cancel = (Button) findViewById(R.id.cancel); cancel.setOnClickListener(new View.OnClickListener() { public void onClick(View v) { BrowserDialog.this.cancel(); } }); Button market = (Button) findViewById(R.id.install_market); market.setOnClickListener(new View.OnClickListener() { public void onClick(View v) { Util.gotoUrl(getContext(), R.string.oi_filemanager_market); BrowserDialog.this.cancel(); } }); if (!isMarketInstalled()) { market.setVisibility(View.GONE); } Button web = (Button) findViewById(R.id.install_web); web.setOnClickListener(new View.OnClickListener() { public void onClick(View v) { Util.gotoUrl(getContext(), R.string.oi_filemanager_web); BrowserDialog.this.cancel(); } }); } private boolean isMarketInstalled() { PackageManager pm = getContext().getPackageManager(); try { pm.getPackageInfo("com.android.vending", 0); } catch (NameNotFoundException e) { return false; } return true; } }