public class NotificationUtil { public static final String COPY_CHANNEL_ID = "copy"; public static final String COPY_CHANNEL_NAME = "Copy username and password"; public static void createChannels(Context ctx) { if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) { NotificationChannel channel = new NotificationChannel(COPY_CHANNEL_ID, COPY_CHANNEL_NAME, NotificationManager.IMPORTANCE_LOW); channel.enableLights(false); channel.enableVibration(false); channel.setLockscreenVisibility(Notification.VISIBILITY_PRIVATE); NotificationManager manager = (NotificationManager) ctx.getSystemService(Context.NOTIFICATION_SERVICE); if (manager == null) return; manager.createNotificationChannel(channel); } } }