public class TypefaceFactory { private static ReferenceMap typefaceMap = new ReferenceMap(AbstractReferenceMap.HARD, AbstractReferenceMap.WEAK); public static Typeface getTypeface(Context ctx, String fontPath) { Typeface tf; tf = (Typeface) typefaceMap.get(fontPath); if (tf != null) { return tf; } try { return Typeface.createFromAsset(ctx.getAssets(), fontPath); } catch (Exception e) { return null; } } }