public class DexClassLoaderHelper { private static class DexClassLoaderHelperHolder { private static final DexClassLoaderHelper INSTANCE = new DexClassLoaderHelper(); } private DexClassLoaderHelper() { } public static DexClassLoaderHelper getInstance() { return DexClassLoaderHelperHolder.INSTANCE; } public ClassLoader getDexClassLoader(PathHolder pathHolder) { ClassLoader myLoader = DexClassLoaderHelper.class.getClassLoader(); return new DexClassLoader(pathHolder.getJarFilePath(), pathHolder.getDirLocation(), null, myLoader); } }