public class MinValue extends ExtremaValueBase { public static final String FIELD_NAME = "min"; public MinValue() { super( 1, new Fields( FIELD_NAME ) ); } @ConstructorProperties({"fieldDeclaration"}) public MinValue( Fields fieldDeclaration ) { super( 1, fieldDeclaration ); } @ConstructorProperties({"fieldDeclaration", "ignoreValues"}) public MinValue( Fields fieldDeclaration, Object... ignoreValues ) { super( fieldDeclaration, ignoreValues ); } @Override protected boolean compare( Comparable lhs, Comparable rhs ) { return lhs.compareTo( rhs ) > 0; } }