public class AssertGroupSizeMoreThan extends AssertGroupBase { @ConstructorProperties({"size"}) public AssertGroupSizeMoreThan( long size ) { super( "group size %s, is less than or equal to: %s, in group %s: %s", size ); } @ConstructorProperties({"patternString", "size"}) public AssertGroupSizeMoreThan( String patternString, long size ) { super( "group matching '%s' with size: %s, is less than or equal to: %s, in group %s: %s", patternString, size ); } @Property(name = "size", visibility = Visibility.PRIVATE) @PropertyDescription("The minimum group size.") @Override public long getSize() { return super.getSize(); } @Override protected boolean assertFails( Long groupSize ) { return groupSize <= size; } }