public class AssertNotNull extends BaseAssertion implements ValueAssertion { public AssertNotNull() { super( "argument '%s' value was null, in tuple: %s" ); } @Override public void doAssert( FlowProcess flowProcess, ValueAssertionCall assertionCall ) { TupleEntry input = assertionCall.getArguments(); int pos = 0; for( Object value : input.getTuple() ) { if( value == null ) fail( input.getFields().get( pos ), input.getTuple().print() ); pos++; } } }