public class AssertGroupSizeEquals extends AssertGroupBase { @ConstructorProperties({"size"}) public AssertGroupSizeEquals( long size ) { super( "group size: %s, is not equal to: %s, in group %s: %s", size ); } @ConstructorProperties({"patternString", "size"}) public AssertGroupSizeEquals( String patternString, long size ) { super( "group matching '%s' with size: %s, is not equal to: %s, in group %s: %s", patternString, size ); } @Property(name = "size", visibility = Visibility.PRIVATE) @PropertyDescription("The expected group size.") @Override public long getSize() { return super.getSize(); } @Override protected boolean assertFails( Long groupSize ) { return groupSize != size; } }