public class NullSafeReverseComparator implements Comparator<Comparable<Object>>, Serializable { public static <T> Comparator<T> reverseOrder( Comparator<T> comparator ) { if( comparator == null ) return (Comparator<T>) new NullSafeReverseComparator(); return Collections.reverseOrder( comparator ); } @Override public int compare( Comparable<Object> lhs, Comparable<Object> rhs ) { if( lhs == null && rhs == null ) return 0; if( lhs == null ) return 1; if( rhs == null ) return -1; return rhs.compareTo( lhs ); } }