public class IntegerNameProvider<V> implements VertexNameProvider<V> { private int nextID = 1; private final Map<V, Integer> idMap = new HashMap<>(); public void clear() { nextID = 1; idMap.clear(); } @Override public String getVertexName( V vertex ) { Integer id = idMap.get( vertex ); if( id == null ) { id = nextID++; idMap.put( vertex, id ); } return id.toString(); } }