public class ValueTuple extends Tuple implements Resettable1<Tuple> { public static final ValueTuple NULL = Tuples.asUnmodifiable( new ValueTuple() ); public ValueTuple( List<Object> elements ) { super( elements ); } public ValueTuple( Fields fields, List<Object> elements ) { super( fields, elements ); } public ValueTuple() { } public ValueTuple( Tuple tuple ) { super( tuple ); } public ValueTuple( Object... values ) { super( values ); } @Override public void reset( Tuple value ) { elements = Tuple.elements( value ); } }