public class DoubleObjectCoerce extends NumberCoerce<Double> { protected DoubleObjectCoerce( Map<Type, Coercions.Coerce> map ) { super( map ); } @Override public Class<Double> getCanonicalType() { return Double.class; } @Override protected Double forNull() { return null; } @Override protected Double forBoolean( Boolean f ) { return f ? 1D : 0D; } @Override protected <T> Double parseType( T f ) { return Double.parseDouble( f.toString() ); } @Override protected Double asType( Number f ) { return f.doubleValue(); } }