public class IntegerObjectCoerce extends NumberCoerce<Integer> { protected IntegerObjectCoerce( Map<Type, Coercions.Coerce> map ) { super( map ); } @Override public Class<Integer> getCanonicalType() { return Integer.class; } @Override protected Integer forNull() { return null; } @Override protected Integer forBoolean( Boolean f ) { return f ? 1 : 0; } @Override protected <T> Integer parseType( T f ) { return Integer.parseInt( f.toString() ); } @Override protected Integer asType( Number f ) { return f.intValue(); } }