public class UniquePathGraphPartitioner extends ExpressionGraphPartitioner { boolean includeRemainders = false; private boolean multiEdge = false; public UniquePathGraphPartitioner( ExpressionGraph contractionGraph, ExpressionGraph expressionGraph, ElementAnnotation... annotations ) { super( contractionGraph, expressionGraph, annotations ); } public UniquePathGraphPartitioner( ExpressionGraph contractionGraph, ExpressionGraph expressionGraph, boolean includeRemainders, ElementAnnotation... annotations ) { super( contractionGraph, expressionGraph, annotations ); this.includeRemainders = includeRemainders; } @Override protected SubGraphIterator wrapIterator( ExpressionSubGraphIterator expressionIterator ) { if( !includeRemainders ) return new UniquePathSubGraphIterator( expressionIterator, true, multiEdge ); return new IncludeRemainderSubGraphIterator( new UniquePathSubGraphIterator( expressionIterator, true, multiEdge ), multiEdge ); } }