public class WholeGraphPartitioner extends GraphPartitioner { public WholeGraphPartitioner() { } public Partitions partition( PlannerContext plannerContext, ElementGraph elementGraph, Collection<FlowElement> excludes ) { Map<ElementGraph, EnumMultiMap> annotatedSubGraphs = new LinkedHashMap<>(); if( elementGraph.containsVertex( Extent.head ) ) elementGraph = new ElementMaskSubGraph( elementGraph, Extent.head, Extent.tail ); annotatedSubGraphs.put( new ElementMultiGraph( elementGraph ), new EnumMultiMap() ); return new Partitions( this, elementGraph, annotatedSubGraphs ); } }