public class RuleExpression { protected final ExpressionGraph contractionExpression; protected final ExpressionGraph contractedMatchExpression; protected final ExpressionGraph matchExpression; public RuleExpression( ExpressionGraph matchExpression ) { this.contractionExpression = null; this.contractedMatchExpression = null; this.matchExpression = matchExpression; verify(); } public RuleExpression( ExpressionGraph contractionExpression, ExpressionGraph matchExpression ) { this.contractionExpression = contractionExpression; this.contractedMatchExpression = null; this.matchExpression = matchExpression; verify(); } public RuleExpression( ExpressionGraph contractionExpression, ExpressionGraph contractedMatchExpression, ExpressionGraph matchExpression ) { this.contractionExpression = contractionExpression; this.contractedMatchExpression = contractedMatchExpression; this.matchExpression = matchExpression; verify(); } private void verify() { } public String getExpressionName() { return getClass().getSimpleName().replaceAll( "^(.*)[]A-Z][a-z]*Rule$", "$1" ); } public ExpressionGraph getContractionExpression() { return contractionExpression; } public ExpressionGraph getContractedMatchExpression() { return contractedMatchExpression; } public ExpressionGraph getMatchExpression() { return matchExpression; } }