public class RuleContractedTransformer extends RuleTransformer { public RuleContractedTransformer( PlanPhase phase, RuleExpression ruleExpression ) { super( phase, ruleExpression ); if( subGraphTransformer != null ) graphTransformer = new ContractedGraphTransformer( subGraphTransformer, ruleExpression.getMatchExpression() ); else if( contractedTransformer != null ) graphTransformer = new ContractedGraphTransformer( contractedTransformer, ruleExpression.getMatchExpression() ); else graphTransformer = new ContractedGraphTransformer( ruleExpression.getMatchExpression() ); } }