public class ProcessModels { public static boolean isIdentity( ProcessModel process, Class<? extends FlowElement> excludingType ) { ElementGraph elementGraph = ElementGraphs.asExtentMaskedSubGraph( process.getElementGraph() ); Set<? extends FlowElement> sourceElements = Util.narrowIdentitySet( excludingType, process.getSourceElements(), false ); Set<? extends FlowElement> sinkElements = Util.narrowIdentitySet( excludingType, process.getSinkElements(), false ); return elementGraph.vertexSet().size() == ( sourceElements.size() + sinkElements.size() ); } public static boolean isIdentity( ProcessModel process ) { ElementGraph elementGraph = ElementGraphs.asExtentMaskedSubGraph( process.getElementGraph() ); Set<FlowElement> sourceElements = process.getSourceElements(); Set<FlowElement> sinkElements = process.getSinkElements(); return elementGraph.vertexSet().size() == ( sourceElements.size() + sinkElements.size() ); } }