public class CloseReducingDuct<Group, Incoming, Outgoing> extends Duct<Incoming, Outgoing> implements CloseWindow, Reducing<Group, Outgoing> { public CloseReducingDuct( Duct<Outgoing, ?> next ) { super( next ); } @Override public void receive( Duct previous, int ordinal, Incoming incoming ) { } @Override public void startGroup( Duct previous, Group group ) { } @Override public void completeGroup( Duct previous, Outgoing outgoing ) { next.receive( previous, 0, outgoing ); } }