public class LazySpillableTupleCollection extends LazyCollection implements Spillable { Spillable spillable; public LazySpillableTupleCollection( Collection<Tuple> parent ) { super( parent ); if( !( parent instanceof Spillable ) ) throw new IllegalArgumentException( "parent is not a Spillable type: " + parent.getClass().getName() ); this.spillable = (Spillable) parent; } @Override public void setGrouping( Tuple group ) { spillable.setGrouping( group ); } @Override public Tuple getGrouping() { return spillable.getGrouping(); } @Override public void setSpillStrategy( SpillStrategy spillStrategy ) { spillable.setSpillStrategy( spillStrategy ); } @Override public void setSpillListener( SpillListener spillListener ) { spillable.setSpillListener( spillListener ); } @Override public int spillCount() { return spillable.spillCount(); } }