public class CountingFlowListener implements FlowListener { public int started = 0; public int stopping = 0; public int completed = 0; public int successful = 0; public int failed = 0; public int stopped = 0; public int skipped = 0; public int thrown = 0; @Override public void onStarting( Flow flow ) { started++; } @Override public void onStopping( Flow flow ) { stopping++; } @Override public void onCompleted( Flow flow ) { completed++; CascadingStats.Status status = flow.getFlowStats().getStatus(); switch( status ) { case PENDING: break; case SKIPPED: skipped++; break; case STARTED: break; case SUBMITTED: break; case RUNNING: break; case SUCCESSFUL: successful++; break; case STOPPED: stopped++; break; case FAILED: failed++; break; } } @Override public boolean onThrowable( Flow flow, Throwable throwable ) { thrown++; return false; } }