public class DefaultKetamaNodeLocatorConfiguration implements KetamaNodeLocatorConfiguration { private final int numReps = 160; private final KetamaNodeKeyFormatter ketamaNodeKeyFormatter; public DefaultKetamaNodeLocatorConfiguration() { ketamaNodeKeyFormatter = new KetamaNodeKeyFormatter(); } public DefaultKetamaNodeLocatorConfiguration(KetamaNodeKeyFormatter nodeKeyFormatter) { ketamaNodeKeyFormatter = nodeKeyFormatter; } public int getNodeRepetitions() { return numReps; } public String getKeyForNode(MemcachedNode node, int repetition) { return ketamaNodeKeyFormatter.getKeyForNode(node, repetition); } }