public class ClassDefFormat2 extends ClassDef { private int classRangeCount; private RangeRecord[] classRangeRecords; public ClassDefFormat2(RandomAccessFileEmulator raf) throws IOException { classRangeCount = raf.readUnsignedShort(); classRangeRecords = new RangeRecord[classRangeCount]; for (int i = 0; i < classRangeCount; i++) { classRangeRecords[i] = new RangeRecord(raf); } } public int getFormat() { return 2; } }