public class UuidUtil { public static String toHexString(UUID uuid) { if (uuid == null) { return null; } byte[] buf = Types.UUIDtoBytes(uuid); if (buf == null) { return null; } int len = buf.length; if (len == 0) { return ""; } StringBuilder sb = new StringBuilder(); short bt; char high, low; for (int i = 0; i < len; i++) { bt = (short)(buf[i] & 0xFF); high = (char)(bt >>> 4); low = (char)(bt & 0x0F); char h,l; h = byteToChar(high); l = byteToChar(low); sb.append(byteToChar(high)); sb.append(byteToChar(low)); } return sb.toString(); } private static char byteToChar(char bt) { if (bt >= 10) { return (char)('A' + bt - 10); } else { return (char)('0' + bt); } } }