public class PathHolder { public static final String DEX_FILE_EXTENSION = ".dex"; public static final String JAR_FILE_EXTENSION = ".jar"; private final String dirLocation; private final String fileNamePrefix; public PathHolder(String dirLocation, String fileNamePrefix) { if (dirLocation == null) { throw new NullPointerException("dirLocation == null"); } if (fileNamePrefix == null) { throw new NullPointerException("fileNamePrefix == null"); } this.dirLocation = dirLocation; this.fileNamePrefix = fileNamePrefix; } public String getFileName() { return fileNamePrefix; } public String getDexFilePath() { return dirLocation + File.separator + fileNamePrefix + DEX_FILE_EXTENSION; } public String getDexFileName() { return fileNamePrefix + DEX_FILE_EXTENSION; } public String getJarFilePath() { return dirLocation + File.separator + fileNamePrefix + JAR_FILE_EXTENSION; } public String getJarFileName() { return fileNamePrefix + JAR_FILE_EXTENSION; } public String getDirLocation() { return dirLocation; } }