public class Blort { private volatile int i; private volatile long l; private volatile float f; private volatile double d; public void blort(int i1, int i2) { i = -i1; i = ~i1; i = i1 + i2; i = i1 - i2; i = i1 * i2; i = i1 / i2; i = i1 % i2; i = i1 & i2; i = i1 | i2; i = i1 ^ i2; i = i1 << i2; i = i1 >> i2; i = i1 >>> i2; } public void blort(long l1, long l2) { l = -l1; l = ~l1; l = l1 + l2; l = l1 - l2; l = l1 * l2; l = l1 / l2; l = l1 % l2; l = l1 & l2; l = l1 | l2; l = l1 ^ l2; l = l1 << l2; l = l1 >> l2; l = l1 >>> l2; } public void blort(float f1, float f2) { f = -f1; f = f1 + f2; f = f1 - f2; f = f1 * f2; f = f1 / f2; f = f1 % f2; } public void blort(double d1, double d2) { d = -d1; d = d1 + d2; d = d1 - d2; d = d1 * d2; d = d1 / d2; d = d1 % d2; } }