public class CstTypeTest { @Test public void checkClearInternTable() { CstType boolArray = CstType.BOOLEAN_ARRAY; assertTrue(boolArray == CstType.intern(Type.BOOLEAN_ARRAY)); CstType myClass = CstType.intern(Type.intern("Lcom/example/Foo;")); CstType.clearInternTable(); Type.clearInternTable(); assertTrue(boolArray == CstType.intern(Type.BOOLEAN_ARRAY)); CstType myClass2 = CstType.intern(Type.intern("Lcom/example/Foo;")); assertEquals(myClass.getClassType(), myClass2.getClassType()); assertFalse(myClass == myClass2); } }