public class TrieTest { @Test public void testGetPrefix() { Trie<String> trie = new Trie<>(); trie.put( "a", "a" ); trie.put( "aa", "aa" ); trie.put( "aaa", "aaa" ); trie.put( "ab", "ab" ); trie.put( "abb", "abb" ); trie.put( "aabb", "aabb" ); assertEquals( null, trie.get( "" ) ); assertEquals( "a", trie.get( "a" ) ); assertEquals( "a", trie.get( "azzz" ) ); assertEquals( "aa", trie.get( "aa" ) ); assertEquals( "aaa", trie.get( "aaa" ) ); assertEquals( "ab", trie.get( "ab" ) ); assertEquals( "abb", trie.get( "abb" ) ); assertEquals( "aabb", trie.get( "aabb" ) ); assertEquals( "aabb", trie.get( "aabbcdef" ) ); assertNull( trie.get( "zzz" ) ); assertNull( trie.get( "" ) ); } @Test public void testHasPrefix() { Trie<String> trie = new Trie<>(); trie.put( "a", "a" ); trie.put( "aa", "aa" ); trie.put( "aaa", "aaa" ); trie.put( "ab", "ab" ); trie.put( "abb", "abb" ); trie.put( "aabb", "aabb" ); assertTrue( trie.hasPrefix( "a" ) ); assertTrue( trie.hasPrefix( "aa" ) ); assertTrue( trie.hasPrefix( "aaa" ) ); assertFalse( trie.hasPrefix( "" ) ); assertFalse( trie.hasPrefix( "5" ) ); assertFalse( trie.hasPrefix( "zzzz" ) ); } @Test public void testGetCommonPrefix() { Trie<String> trie = new Trie<>(); trie.put( "a", "a" ); trie.put( "aa", "aa" ); trie.put( "aaa", "aaa" ); trie.put( "aabb", "aabb" ); trie.put( "aaabb", "aaabb" ); trie.put( "aacbb", "aacbb" ); assertEquals( "aa", trie.getCommonPrefix() ); } @Test public void testGetCommonPrefixNone() { Trie<String> trie = new Trie<>(); trie.put( "a", "a" ); trie.put( "b", "a" ); trie.put( "c", "a" ); trie.put( "e", "a" ); trie.put( "f", "a" ); assertEquals( "", trie.getCommonPrefix() ); } }