public class MaxValue extends ExtremaValueBase { public static final String FIELD_NAME = "max"; public MaxValue() { super( 1, new Fields( FIELD_NAME ) ); } @ConstructorProperties({"fieldDeclaration"}) public MaxValue( Fields fieldDeclaration ) { super( 1, fieldDeclaration ); } @ConstructorProperties({"fieldDeclaration", "ignoreValues"}) public MaxValue( Fields fieldDeclaration, Object... ignoreValues ) { super( fieldDeclaration, ignoreValues ); } @Override protected boolean compare( Comparable lhs, Comparable rhs ) { return lhs.compareTo( rhs ) < 0; } }