public class BigDecimalCoerce extends Coercions.Coerce<BigDecimal> { protected BigDecimalCoerce( Map<Type, Coercions.Coerce> map ) { super( map ); } @Override public Class<BigDecimal> getCanonicalType() { return BigDecimal.class; } @Override public BigDecimal coerce( Object value ) { if( value instanceof Double ) return BigDecimal.valueOf( (Double) value ); else if( value instanceof Long ) return BigDecimal.valueOf( (Long) value ); else if( value == null || value.toString().isEmpty() ) return null; else return new BigDecimal( value.toString() ); } }