public class PartitionTapProps extends Props { public static final String FAIL_ON_CLOSE = "cascading.tap.partition.failonclose"; private boolean failOnClose = false; public static void setFailOnClose( Map<Object, Object> properties, boolean failOnClose ) { properties.put( FAIL_ON_CLOSE, Boolean.toString( failOnClose ) ); } public static PartitionTapProps partitionTapProps() { return new PartitionTapProps(); } public PartitionTapProps() { } public boolean isFailOnClose() { return failOnClose; } public PartitionTapProps setFailOnClose( boolean failOnClose ) { this.failOnClose = failOnClose; return this; } @Override protected void addPropertiesTo( Properties properties ) { setFailOnClose( properties, failOnClose ); } }