public class AppSettingsActivity extends LockCloseActivity { public static void Launch(Context ctx) { Intent i = new Intent(ctx, AppSettingsActivity.class); ctx.startActivity(i); } @Override protected void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); setContentView(R.layout.settings); if (savedInstanceState == null) { getSupportFragmentManager() .beginTransaction() .replace(R.id.settings, new MainSettingsFragement()) .commit(); } } }