public class TypeTest { @Test public void testClearInternTable() { Type boolArray = Type.BOOLEAN.getArrayType(); assertTrue(boolArray == Type.BOOLEAN_ARRAY); assertTrue(boolArray == Type.intern("[Z")); Type myClass = Type.intern("Lcom/example/Foo;"); Type.clearInternTable(); assertTrue(boolArray == Type.intern("[Z")); Type myClass2 = Type.intern("Lcom/example/Foo;"); assertEquals(myClass, myClass2); assertFalse(myClass == myClass2); } }